mapper.select:
SELECT r.id AS id, u.name AS userName, r.room_id AS roomId, r.rating AS rating, r.content AS content, r.created_at AS createdAt, NULL AS imgUrl FROM reviews r JOIN users u ON r.user_id = u.id WHERE r.room_id = #{roomId}
AND r.content LIKE '%' || #{keyword} || '%'
AND u.name LIKE '%' || #{userName} || '%'
AND r.rating = #{rating}
ORDER BY r.created_at DESC OFFSET #{offset} ROWS FETCH NEXT #{size} ROWS ONLY

SELECT COUNT(*) FROM reviews r JOIN users u ON r.user_id = u.id WHERE r.room_id = #{roomId}
AND r.content LIKE '%' || #{keyword} || '%'
AND u.name LIKE '%' || #{userName} || '%'
AND r.rating = #{rating}
