mapper.select:
SELECT r.id, NVL(u.name, '익명') AS userName, r.room_id AS roomId, r.rating, CASE WHEN r.content IS NULL THEN NULL ELSE DBMS_LOB.SUBSTR(r.content, 4000, 1) END AS content, r.img_url AS imgUrl, TO_CHAR(r.created_at, 'YYYY-MM-DD HH24:MI:SS') AS createdAt FROM reviews r LEFT JOIN users u ON r.user_id = u.id WHERE 1=1
AND r.room_id = #{roomId, jdbcType=NUMERIC}
AND LOWER(DBMS_LOB.SUBSTR(r.content, 4000, 1)) LIKE '%' || LOWER(#{keyword}) || '%'
AND LOWER(NVL(u.name, '')) LIKE '%' || LOWER(#{userName}) || '%'
AND r.rating = #{rating, jdbcType=NUMERIC}
ORDER BY r.created_at DESC OFFSET #{offset, jdbcType=INTEGER} ROWS FETCH NEXT #{limit, jdbcType=INTEGER} ROWS ONLY

SELECT COUNT(*) FROM reviews r LEFT JOIN users u ON r.user_id = u.id WHERE 1=1
AND r.room_id = #{roomId, jdbcType=NUMERIC}
AND LOWER(DBMS_LOB.SUBSTR(r.content, 4000, 1)) LIKE '%' || LOWER(#{keyword}) || '%'
AND LOWER(NVL(u.name, '')) LIKE '%' || LOWER(#{userName}) || '%'
AND r.rating = #{rating, jdbcType=NUMERIC}
